SET NAMES utf8mb4;
SET FOREIGN_KEY_CHECKS = 0;

DROP TABLE IF EXISTS `handle_register`;
CREATE TABLE `handle_register`  (
    `handle_id` bigint(64) UNSIGNED NOT NULL,
    `handle_name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '句柄名称',
    `handle_type` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '句柄类型',
    `handle_content` JSON COMMENT '句柄内容',
    PRIMARY KEY (`handle_id`) USING BTREE,
    UNIQUE INDEX `name`(`handle_name`) USING BTREE,
    INDEX `pid`(`handle_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 59 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '句柄注册表' ROW_FORMAT = COMPACT;

DROP TABLE IF EXISTS `eletricity_info`;
CREATE TABLE `eletricity_info`  (
                                    `CUSTOMERID` int(8) UNSIGNED NOT NULL,
                                    `DISC` varchar(60) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
                                    `HH` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
                                    `CODE` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
                                    `TYPE` int(1) NOT NULL,
                                    `POWERSUPPLYID` int(6) NOT NULL,
                                    `LINEID` int(8) NOT NULL,
                                    `LINEID2` int(8),
                                    `CUSTOMERSTATEID` int(2) NOT NULL,
                                    `MEASUREMODE` int(4) NOT NULL,
                                    `ECONOMICPROTID` int(4) NOT NULL,
                                    `DEMANDPROTID` int(4) NOT NULL,
                                    `BUSINESSCLASSID` int(4) NOT NULL,
                                    `CAPABILITY` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
                                    `CREATETIME` DATETIME,
                                    `ADDRESS` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
                                    `LINKMAN` varchar(30) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
                                    `TELEPHONE` varchar(30) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
                                    `MOBILE` varchar(30) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
                                    `NOTE` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
                                    `LASTTIME` DATETIME,
                                    PRIMARY KEY (`CUSTOMERID`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 59 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = COMPACT;

